import logging

from django.apps import apps
from django.test import TestCase, Client
from django.urls import reverse
from ..models import Attendance, JoadClass
from student_app.models import Student

logger = logging.getLogger(__name__)
User = apps.get_model('student_app', 'User')


class TestsWaiver(TestCase):
    fixtures = ['f1', 'joad1']

    def setUp(self):
        # Every test needs a client.
        self.client = Client()
        self.test_user = User.objects.get(pk=1)
        self.client.force_login(self.test_user)
        self.img = {'signature': 'data:image/jpeg;base64,',
                    'sig_first_name': 'tester', 'sig_last_name': 'tester'}
        self.invalid_sig = 'data:image/jpeg;base64,'

    def _set_session(self, class_id):
        session = self.client.session
        session['joad_class'] = class_id
        session.save()

    def test_get_waiver_page_bad(self):
        response = self.client.get(reverse('joad:waiver', kwargs={'student_id': 50}), secure=True)
        self.assertEqual(response.status_code, 404)

    def test_get_waiver_page_good(self):
        self._set_session(1)
        response = self.client.get(reverse('joad:waiver', kwargs={'student_id': 10}), secure=True)
        self.assertEqual(response.status_code, 200)
        self.assertTemplateUsed(response, 'program_app/class_sign_in.html')

    def test_post_sign_in_page_good(self):
        self._set_session(1)
        response = self.client.post(reverse('joad:waiver', kwargs={'student_id': 10}), self.img, secure=True)
        self.assertEqual(response.status_code, 302)
        a = Attendance.objects.get(pk=1)
        self.assertTrue(a.attended)

    def test_post_sign_in_page_none(self):
        self._set_session(1)
        response = self.client.post(reverse('joad:waiver', kwargs={'student_id': 10}), secure=True)
        self.assertEqual(response.status_code, 200)
        a = Attendance.objects.all()
        self.assertEqual(len(a), 0)

    def test_get_waiver_page_no_auth(self):
        self.test_user = User.objects.get(pk=3)
        self.client.force_login(self.test_user)
        response = self.client.get(reverse('joad:waiver', kwargs={'student_id': 10}), secure=True)
        self.assertEqual(response.status_code, 403)

    def test_post_sign_in_page_no_class(self):
        response = self.client.post(reverse('joad:waiver', kwargs={'student_id': 10}), self.img, secure=True)
        self.assertEqual(response.status_code, 302)
        a = Attendance.objects.get(pk=1)
        self.assertTrue(a.attended)

    def test_post_sign_in_attending(self):
        jc = JoadClass.objects.get(pk=1)
        student = Student.objects.get(pk=10)
        a = Attendance(joad_class=jc, student=student, attended=True)
        a.save()
        self._set_session(1)
        response = self.client.post(reverse('joad:waiver', kwargs={'student_id': 10}), self.img, secure=True)
        a = Attendance.objects.get(pk=1)
        self.assertTrue(a.attended)
