import uuid

from django.apps import apps
from django.test import TestCase, Client
from django.urls import reverse
from django.utils import timezone
from unittest.mock import patch

from student_app.tests.helper import remove_signatures
from ..models import BeginnerClass
from event.models import Registration
from event.models import Event
from student_app.models import Student
from payment.models import PaymentLog
from payment.tests import MockSideEffects

import logging
logger = logging.getLogger(__name__)
User = apps.get_model('student_app', 'User')


class TestsClassSignIn(MockSideEffects, TestCase):
    fixtures = ['f1', 'f2']

    def setUp(self):
        # Every test needs a client.
        self.client = Client()
        self.test_user = User.objects.get(pk=1)
        self.client.force_login(self.test_user)
        self.img = {'signature': 'data:image/jpeg;base64,',
                    'sig_first_name': 'tester', 'sig_last_name': 'tester'}

        self.invalid_sig = 'data:image/jpeg;base64,'

    def tearDown(self):
        # remove any files we made.
        remove_signatures()

    def test_get_sign_in_page_bad(self):
        response = self.client.get(reverse('programs:class_sign_in', kwargs={'reg_id': 5}), secure=True)
        self.assertEqual(response.status_code, 404)

    def test_get_sign_in_page_good(self):
        response = self.client.get(reverse('programs:class_sign_in', kwargs={'reg_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        self.assertTemplateUsed(response, 'program_app/class_sign_in.html')

    def test_post_sign_in_page_good(self):
        response = self.client.post(reverse('programs:class_sign_in', kwargs={'reg_id': 1}), self.img, secure=True)
        self.assertEqual(response.status_code, 302)
        cr = Registration.objects.get(pk=1)
        self.assertTrue(cr.student.signature)
        self.assertEqual(cr.student.safety_class, timezone.datetime(year=2023, month=6, day=5).date())

    def test_post_sign_in_page_none(self):
        response = self.client.post(reverse('programs:class_sign_in', kwargs={'reg_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        cr = Registration.objects.get(pk=1)
        self.assertFalse(cr.student.signature)

    @patch('program_app.views.class_sign_in_view.RefundHelper.refund_with_idempotency_key')
    def test_sign_in_unregister(self, refund):
        ik = uuid.uuid4()
        bc2 = BeginnerClass.objects.get(pk=2)
        reg2 = Registration.objects.create(
            event=bc2.event,
            student=Student.objects.get(pk=2),
            pay_status="paid",
            idempotency_key=ik,
            reg_time="2021-06-09",
            attended=False
        )
        payment = PaymentLog.objects.create(
            category='intro',
            checkout_created_time=timezone.now(),
            description='programs_test',  # database set to 255 characters
            donation=0,  # storing pennies in the database
            idempotency_key=ik,
            location_id='',
            order_id='',
            payment_id='test_payment',
            receipt='',
            source_type='',
            status='SUCCESS',
            total_money=bc2.event.cost_standard*100,
            user=self.test_user
        )
        bc3 = BeginnerClass.objects.create(
            class_type="beginner",
            beginner_limit=0,
            beginner_wait_limit=10,
            returnee_limit=0,
            returnee_wait_limit=0,
            event=Event.objects.create(
                event_date="2023-06-05T16:00:00.000Z",
                cost_standard=5,
                cost_member=5,
                state="wait",
                type='class'
            ),
        )
        reg3 = Registration.objects.create(
            event=bc3.event,
            student=Student.objects.get(pk=2),
            pay_status="waiting",
            idempotency_key='3239ed71-6740-4540-86f3-86fff79898a1',
            reg_time="2021-06-09",
            attended=False
        )
        response = self.client.post(reverse('programs:class_sign_in', kwargs={'reg_id': 1}), self.img, secure=True)
        self.assertEqual(response.status_code, 302)
        cr = Registration.objects.get(pk=1)
        self.assertTrue(cr.student.signature)
        self.assertEqual(cr.student.safety_class, timezone.datetime(year=2023, month=6, day=5).date())
        cr3 = Registration.objects.get(pk=reg3.id)
        self.assertEqual(cr3.pay_status, 'canceled')
        refund.assert_called_with(ik, 500)
