import logging

from django.core import mail
from django.test import TestCase, Client
from django.urls import reverse
from payment.src import EmailMessage
from ..models import ClassRegistration, User

logger = logging.getLogger(__name__)


class TestsClassSignIn(TestCase):
    fixtures = ['f1', 'f2']

    def setUp(self):
        # Every test needs a client.
        self.client = Client()
        self.test_user = User.objects.get(pk=1)
        self.client.force_login(self.test_user)
        self.img = {'signature': 'data:image/jpeg;base64,'}


    def test_get_sign_in_page_bad(self):
        response = self.client.get(reverse('registration:class_sign_in', kwargs={'reg_id': 5}), secure=True)
        self.assertEqual(response.status_code, 404)

    def test_get_sign_in_page_good(self):
        response = self.client.get(reverse('registration:class_sign_in', kwargs={'reg_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        self.assertTemplateUsed(response, 'student_app/class_sign_in.html')

    def test_post_sign_in_page_good(self):
        response = self.client.post(reverse('registration:class_sign_in', kwargs={'reg_id': 1}), self.img, secure=True)
        self.assertEqual(response.status_code, 200)
        cr = ClassRegistration.objects.get(pk=1)
        self.assertTrue(cr.signature)

    def test_post_sign_in_page_none(self):
        response = self.client.post(reverse('registration:class_sign_in', kwargs={'reg_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        cr = ClassRegistration.objects.get(pk=1)
        self.assertFalse(cr.signature)
