import logging

from django.apps import apps
from django.test import TestCase, Client
from django.urls import reverse
from django.core import mail
from .helper import remove_signatures
from ..models import Student
from ..src import EmailMessage
from ..tasks import waiver_pdf

logger = logging.getLogger(__name__)
User = apps.get_model('student_app', 'User')


class TestsWaiver(TestCase):
    fixtures = ['f1']
    url = 'registration:waiver'

    def setUp(self):
        # Every test needs a client.
        self.client = Client()
        self.test_user = User.objects.get(pk=1)
        self.client.force_login(self.test_user)
        self.img = {'signature': 'data:image/jpeg;base64,',
                    'sig_first_name': 'tester', 'sig_last_name': 'tester'}

        self.invalid_sig = 'data:image/jpeg;base64,'

    def tearDown(self):
        # remove any files we made.
        remove_signatures()

    def test_get_waiver_bad(self):
        response = self.client.get(reverse(self.url, kwargs={'student_id': 15}), secure=True)
        self.assertEqual(response.status_code, 404)

    def test_get_waiver_good(self):
        response = self.client.get(reverse(self.url, kwargs={'student_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        self.assertTemplateUsed(response, 'program_app/class_sign_in.html')

    def test_post_waiver_good(self):
        # test task no signature
        student = Student.objects.get(pk=1)
        waiver_pdf(student.id, student.first_name, student.last_name)
        self.assertEqual(len(mail.outbox), 0)

        response = self.client.post(reverse(self.url, kwargs={'student_id': 1}), self.img, secure=True)
        self.assertEqual(response.status_code, 302)
        student = Student.objects.get(pk=1)
        self.assertTrue(student.signature)
        self.assertIsNotNone(student.signature_pdf)

        # test task
        waiver_pdf(student.id, student.first_name, student.last_name)
        self.assertEqual(len(mail.outbox), 1)

    def test_post_waiver_invalid(self):
        self.img['signature'] = self.invalid_sig
        response = self.client.post(reverse(self.url, kwargs={'student_id': 1}), self.img, secure=True)
        self.assertFormError(response, 'form', None, 'invalid signature')
        student = Student.objects.get(pk=1)
        self.assertFalse(student.signature)

    def test_post_waiver_none(self):
        student = Student.objects.get(pk=1)
        response = self.client.post(reverse(self.url, kwargs={'student_id': 1}), secure=True)
        self.assertEqual(response.status_code, 200)
        student = Student.objects.get(pk=1)
        self.assertFalse(student.signature)

    def test_email_waiver_no_pdf(self):
        student = Student.objects.get(pk=1)
        em = EmailMessage()
        em.awrl_email(student)
        self.assertEqual(len(mail.outbox), 0)
